mapper.update:
UPDATE MOVIE 
SET MOVIE_PLAYED = MOVIE_PLAYED + 1
WHERE MOVIE_NO = #{movieNo}

mapper.select:
SELECT s.SCREEN_NO
FROM SCREEN s
INNER JOIN CINEMA c ON s.CINEMA_NAME = c.CINEMA_NAME
WHERE c.CINEMA_NAME = #{cinemaIndex}
AND s.SCREEN_NAME = #{screenIndex}

mapper.update:
UPDATE PLAY
SET PLAY_BOOKCOUNT = #{playBookCount}, PLAY_BOOKSEAT = #{playBookSeat}
WHERE PLAY_NO = #{playNo}

mapper.select:
SELECT BOOK_PRICE
FROM BOOK
WHERE BOOK_NO = #{bookNo}

SELECT BOOK_SEAT
FROM BOOK
WHERE BOOK_NO = #{bookNo}

SELECT *
FROM STORE_COUPON
WHERE COUPON_DETAIL_NO = #{couponNo}

mapper.update:
        UPDATE STORE_COUPON
		SET COUPON_ST = 'N'
		WHERE COUPON_DETAIL_NO = #{couponDetailNo}

mapper.select:
SELECT RESERVE_CODE_NO
FROM PAY_ORDER
WHERE BOOK_NO = #{bookNo}

SELECT PLAY_NO
FROM BOOK
WHERE BOOK_NO = #{bookNo}

SELECT *
FROM BOOK
WHERE BOOK_NO = #{bookNo}

mapper.update:
    UPDATE Movie
	SET MOVIE_WATCHED = #{movieWatched}
	WHERE MOVIE_NO = #{movieNo}

    UPDATE BOOK
	SET BOOK_ST = 'Y'
	WHERE BOOK_NO = #{bookNo}
